mapper.select:
SELECT id, doc_id as docId, user_id as userId, permission_type as permissionType, create_time as createTime
FROM t_doc_permission
WHERE doc_id = #{docId}

SELECT doc_id as docId
FROM t_doc_permission
WHERE user_id = #{userId}

    AND permission_type = #{permissionType}

SELECT id, doc_id as docId, user_id as userId, permission_type as permissionType, create_time as createTime
FROM t_doc_permission
WHERE doc_id = #{docId} AND user_id = #{userId}
